export const sortByTrimDateAndAppTime=(e,t,r=!0)=>{const a=new Date(`${e.trim_date} ${e.app_time}`),n=new Date(`${t.trim_date} ${t.app_time}`);return r?a-n:n-a};export const sortByDateOnly=(e,t,r=!0)=>{let a=new Date(e),n=new Date(t);if(r){if(a<n)return-1;if(a>n)return 1}else{if(a>n)return-1;if(a<n)return 1}return 0};export function formatDate(e,t,r=!1){const a=new Date(e+"T00:00:00");if(isNaN(a.getTime()))throw new Error("Invalid date");const n=String(a.getDate()).padStart(2,"0"),u=String(a.getMonth()+1).padStart(2,"0"),i=a.getFullYear(),o=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][a.getDay()],s=e=>{switch(e){case"Y-m-d":default:return`${i}-${u}-${n}`;case"m-d-Y":return`${u}-${n}-${i}`;case"d-m-Y":return`${n}-${u}-${i}`}};return r?`${o}<br>${s(t)}`:s(t)}export const getSMSWeekday=e=>{let[t,r,a]=e.split("-").map(Number);return["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][new Date(t,r-1,a).getDay()]};export function formatTime(e,t){let r=e.trim().match(/([01]?[0-9]|2[0-3]):([0-5][0-9])(:[0-5][0-9])?/);if(12==t){if(12==r[1])var a=r[1]+":"+r[2]+" pm";else if(r[1]>12)a=r[1]-12+":"+r[2]+" pm";else{a=(a=r[1]+":"+r[2]+" am").replace(/^0+/,"")}return a}return r[1]+":"+r[2]}export function getReadableCurrentFutureDate(e=null){const t=new Date;let r;return t.setHours(0,0,0,0),r=e?new Date(t.getTime()+24*e*60*60*1e3):t,r.toISOString().split("T")[0]}export function getCurrentTime(){const e=new Date;let t=e.getHours(),r=e.getMinutes();return t=t<10?"0"+t:t,r=r<10?"0"+r:r,t+":"+r}