// @ts-nocheck
export default function getAllFormIdElements(t){try{const e="string"==typeof t?document.getElementById(t):t instanceof HTMLElement?t:null;if(!e)throw new Error("string"==typeof t?`No form found with id: ${t}`:"Invalid form parameter");return Object.fromEntries(Array.from(e.querySelectorAll("input[id], select[id], textarea[id]"),(t=>[t.id,t])))}catch(t){throw t}}